//------------------------------------------------------------------------------
/// Serial debug mode
/// @param  MFC_SERIAL_DEBUG_CMD_e \b IN: command
/// @return TRUE/FALSE
//------------------------------------------------------------------------------
BOOL MDrv_MFC_SetSerialDebugMode(MFC_SERIAL_DEBUG_CMD_e cmd)
{
    U32  u32Addr;
    U8   u8Data[5];
    BOOL ret;

    u32Addr = 0;

    if (cmd == MFC_SerialDebugEnter)
    {
        u8Data[0] = 0x53;
        u8Data[1] = 0x45;
        u8Data[2] = 0x52;
        u8Data[3] = 0x44;
        u8Data[4] = 0x42;
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        ret = MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0,  u8Data, 5);
#else
        ret = MDrv_MFC_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0,  u8Data, 5);
#endif
        if (!ret)
            return FALSE;
    }
    else
    {
        u8Data[0] = cmd;
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        ret = MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0, u8Data, 1);
#else
        ret = MDrv_MFC_IIC_Write(MFC_IIC_CHANNEL_ID, MFC_IIC_SLAVE_ADDR, (U8*)&u32Addr, 0, u8Data, 1);
#endif
        if (!ret)
            return FALSE;
    }
    return TRUE;
}

static BOOL isI2cOpen=FALSE;
void MDrv_MFC_SetSerialDebug(BOOL bOpen)
{
    if(bOpen)
    {
        if(isI2cOpen==FALSE)
        {
            MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugEnter);
            MDrv_MFC_SetSerialDebugMode(MFC_CpuWait);
            MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBus);
            isI2cOpen=TRUE;
        }
    }
    else
    {
        if(isI2cOpen)
        {
            MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
            MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
            MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
            isI2cOpen=FALSE;
        }
    }

}


//------------------------------------------------------------------------------
/// Read register
/// @param  u16Addr \b IN: address
/// @return  u8Val \b OUT: value
//------------------------------------------------------------------------------
U8 MDrv_MFC_ReadByte(U16 u16Addr)
{
    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        return MAdp_MPIF_ReadByte(MFC_SPIF_SLAVE_ID, u16Addr);
#endif
    }
    else
    {
        U8   u8Addr[3], u8buf=0;
        BOOL ret;

        u8Addr[0] = 0x10;
        u8Addr[1] = (U8)(u16Addr >> 8);
        u8Addr[2] = (U8)(u16Addr & 0x00FF);
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 0, u8Addr, 3);
        ret = MAdp_IIC_Read(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 0, &u8buf, 1);
#else
        MDrv_MFC_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 0, u8Addr, 3);
        ret = MDrv_MFC_IIC_Read(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 0, &u8buf, 1);
#endif
        //printf("[MFC R] addr=0x%x, data=0x%x, ret=%u\n", u16Addr, u8buf, ret);
        if (!ret)
            return 0;
        return u8buf;
    }
}

U16 MDrv_MFC_Read2Bytes(U16 u16Reg)
{
    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        return MAdp_MPIF_Read2Byte(MFC_SPIF_SLAVE_ID, u16Reg);
#endif
    }
    else
    {
        U8 u8ReadValue1=0, u8ReadValue2=0;
        u8ReadValue1 = MDrv_MFC_ReadByte(u16Reg + 1);
        u8ReadValue2 = MDrv_MFC_ReadByte(u16Reg);

        return MAKEWORD(u8ReadValue1, u8ReadValue2);
    }
}

U32 MDrv_MFC_Read3Bytes(U16 u16Reg)
{
    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        return MAdp_MPIF_Read3Byte(MFC_SPIF_SLAVE_ID, u16Reg);
#endif
    }
    else
    {
        U8     u8ReadValue1=0;
        U32  u32ReadValue2=0;
        u8ReadValue1 = MDrv_MFC_ReadByte(u16Reg + 2);
        u32ReadValue2 |= ((U32)u8ReadValue1)<<16;
        u8ReadValue1 = MDrv_MFC_ReadByte(u16Reg + 1);
        u32ReadValue2 |= ((U32)u8ReadValue1)<<8;
        u8ReadValue1 = MDrv_MFC_ReadByte(u16Reg);
        u32ReadValue2 |= ((U32)u8ReadValue1);
        return u32ReadValue2;
    }
}
//------------------------------------------------------------------------------
/// Write register
/// @param  u16Addr \b IN: address
/// @param  u8Val   \b IN: value
/// @return TRUE/FALSE
//------------------------------------------------------------------------------
BOOL MDrv_MFC_WriteByte(U16 u16Addr, U8 u8Val)
{
    MFC_REG_DEBUG(printf("\n addr=0x%x, write=0x%x", u16Addr, u8Val));

    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
    #if 1
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        return MAdp_MPIF_WriteByte(MFC_SPIF_SLAVE_ID, u16Addr, u8Val);
#endif
    #else
        // for debug
        U8 ret;
        U8 u8ReadVal;

        ret = MAdp_MPIF_WriteByte(MFC_SPIF_SLAVE_ID, u16Addr, u8Val);
        u8ReadVal = MAdp_MPIF_ReadByte(MFC_SPIF_SLAVE_ID, u16Addr);
        if (u8ReadVal == u8Val)
            MFC_REG_DEBUG(printf(", [OK]\n"));
        else
            MFC_REG_DEBUG(printf(", [NG]read=0x%x\n", u8ReadVal));

        if (!ret)
            return FALSE;
        return TRUE;
    #endif
    }
    else
    {
        U8   u8Addr[3];
        BOOL ret;

        u8Addr[0] = 0x10;
        u8Addr[1] = (U8)(u16Addr >> 8);
        u8Addr[2] = (U8)(u16Addr & 0x00FF);
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        ret = MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 3, &u8Val, 1);
#else
        ret = MDrv_MFC_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 3, &u8Val, 1);
#endif
        if (!ret)
            return FALSE;
        return TRUE;
    }
}

void MDrv_MFC_Write2Bytes(U16 u16Reg, U16 u16Value)
{
    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        MAdp_MPIF_Write2Byte(MFC_SPIF_SLAVE_ID, u16Reg, u16Value);
#endif
    }
    else
    {
        MDrv_MFC_WriteByte(u16Reg, (U8)u16Value);
        MDrv_MFC_WriteByte(u16Reg + 1, (U8)(u16Value / 0x100));
    }
}

void MDrv_MFC_Write3Bytes(U16 u16Reg, U32 u32Value)
{
    if (0)//gmfcSysInfo.u8UseMPIF == 2)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        MAdp_MPIF_Write3Byte(MFC_SPIF_SLAVE_ID, u16Reg, u32Value);
#endif
    }
    else
    {
        MDrv_MFC_WriteByte(u16Reg, (U8)u32Value);
        MDrv_MFC_WriteByte(u16Reg + 1, (U8)(u32Value / 0x100));
        MDrv_MFC_WriteByte(u16Reg + 2, (U8)(u32Value / 0x10000));
    }
}

void MDrv_MFC_WriteBit(U16 u16Addr, U8 u8Bit, U8  u8BitPos)
{
    U8 u8Value;

    u8Value= MDrv_MFC_ReadByte(u16Addr);
    if(u8Bit)
        u8Value = u8Value | u8BitPos;
    else
        u8Value = u8Value & (~u8BitPos);
    MDrv_MFC_WriteByte(u16Addr, u8Value);
}

void MDrv_MFC_WriteByteMask(U16 u16Addr, U8 u8Val, U8 u8Mask)
{
    U8 uc;

    uc = MDrv_MFC_ReadByte(u16Addr);
    uc &= (~u8Mask);
    MDrv_MFC_WriteByte( u16Addr, ( uc | (u8Val & u8Mask)) );
}

void MDrv_MFC_WriteRegsTbl(U16 wIndex, MST_MFC_RegUnitType_t *pTable)
{
    while (1)
    {
         if (pTable->ucIndex == _END_OF_TBL_
          && pTable->ucValue == _END_OF_TBL_) // check end of table
             break;

         MDrv_MFC_WriteByte(wIndex | pTable->ucIndex, pTable->ucValue); // write register
         pTable++;// next
    }
}

