//------------------------------------------------------------------------------
/// MFC Load Bin to SRAM
//------------------------------------------------------------------------------
BOOL MDrv_MFC_LoadBinToSram(U8* pBin, U32 len)
{
    U8   i;
    U8   u8Addr[3];
    BOOL ret;

    len=len;
    MDrv_MFC_WriteByte(0x10E6, 0x01); // enable xdata mapping to psam
    for (i=0; i<16; i++)
    {
        // Switch page, every page is 1k bytes
        ret = MDrv_MFC_WriteByte(0x10E4, i); // Switch page, every page is 1k bytes
        if (!ret)
            return FALSE;

        // downloading
        u8Addr[0] = 0x10;
        u8Addr[1] = 0x50;
        u8Addr[2] = 0x00;
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        ret = MAdp_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 3, pBin, 1024);
#else
        ret = MDrv_MFC_IIC_Write(MFC_IIC_CHANNEL_ID, 0xB4, u8Addr, 3, pBin, 1024);
#endif
        if (!ret)
            return FALSE;

        pBin += 1024;
    }
    MDrv_MFC_WriteByte(0x10E4, 0x00); // Switch page, every page is 1k bytes
    MDrv_MFC_WriteByte(0x10E6, 0x00); // disable xdata mapping to psam

    return TRUE;
}

BOOL MDrv_MFC_WaitReady(U8 u8WaitValue)
{
    U8 u8ReadValue, u8count = 0;
    while(u8count++ < 10)
    {
#if(CODEBASE_SEL == CODEBASE_LINUX)
        usleep(100 * 1000);
#elif(CODEBASE_SEL == CODEBASE_UTOPIA)
        OS_DELAY_TASK(100);
#endif
        u8ReadValue=0x00;
        MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugEnter);
        MDrv_MFC_SetSerialDebugMode(MFC_CpuWait);
        MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBus);

        u8ReadValue = MDrv_MFC_ReadByte(0x2C45);
        //thchen printf("\n\n\n\n\n\n\n[0x2C45]=[%x]\n\n\n\n\n", u8ReadValue);

        MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
        MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
        MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
        if(u8ReadValue == u8WaitValue)
            break;
    }
    return (u8count < 10) ? 1 : 0;
}

void MDrv_MFC_SramLoadCode(void)
{
    U8 *pAddr;
    U32 Len;
    pAddr = MFC_BIN;
    Len = sizeof(MFC_BIN);
    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugEnter);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWait);
    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBus);

    MDrv_MFC_Write2Bytes(0x3C60, 0xAA55);//Disable watch dog
    MDrv_MFC_LoadBinToSram(pAddr, Len);
    MDrv_MFC_WriteByte(0x1084, 0x01); // Sram boot code reset
    MDrv_MFC_WriteByte(0x2C45, 0x01);

    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
    MDrv_MFC_WaitReady(2);
}

