void MDrv_MFC_BringUp(void)
{
    // I2C or MPIF
    gmfcSysInfo.u8UseMPIF = 0;
    //thchen printf("gmfcSysInfo.u8UseMPIF=%u\n", gmfcSysInfo.u8UseMPIF);

    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugEnter);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWait);
    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBus);

    MDrv_MFC_SetInterrupt(_DISABLE);
    MDrv_MFC_McuInitialize();
    MDrv_MFC_WriteBit(0x2F00, 0, _BIT3);    //Dubber buffer dis
    MDrv_MFC_WriteBit(0x1E03, 1, _BIT0);    //SCaler reset Hold
    MDrv_MFC_WriteBit(0x1E49, 0, _BIT0);    //clear this status
    MDrv_MFC_InitializeChip();

    // SPIF settings
    MDrv_MFC_WriteByteMask(0x1E38, _BIT3, _BIT3);    // swap M/SPIF
    MDrv_MFC_WriteByte(0x1E8D, 0x18);   // set SPIF clock, 0x18: 12MHz
  //MDrv_MFC_WriteByte(0x1E8D, 0x10);   // set SPIF clock, 0x10: 108MHz

    if (gmfcSysInfo.u8UseMPIF)
    {
#if(CODEBASE_SEL != CODEBASE_UTOPIA)
        //MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
        //MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
        //MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
        MAdp_MPIF_Open();
        MAdp_MPIF_Init(0x3C, 0x1, 0x1); //0x3C: 12MHz
      //MAdp_MPIF_Init(0x04, 0x1, 0x1); //0x04:123MHz
        MAdp_MPIF_InitSPIF(MFC_SPIF_SLAVE_ID);
        MAdp_MPIF_SetCmdDataWidth(MFC_SPIF_SLAVE_ID, MPIF_DATAWIDTH_1, MPIF_DATAWIDTH_1);
        gmfcSysInfo.u8UseMPIF = 2;
#endif
    }

    MDrv_MFC_InitializeScalerOP();
    MDrv_MFC_InitializeMiu();


    MDrv_MFC_InitializeScalerIP();
    MDrv_MFC_InitializeFilmMode();
    MDrv_MFC_InitializePanel();


	MDrv_MFC_PowerDownChipU3();
    MDrv_MFC_WriteBit(0x2F00, 1, _BIT3);	//Dubber buffer En
	MDrv_MFC_SetInterrupt(_ENABLE);
    if(gmfcSysInfo.u8PanelType == _LVDS)
    {
        MDrv_MFC_Write2Bytes(0x3220, 0x5555); // set output configure to lvds output = "01" for 26 channel
    	MDrv_MFC_Write2Bytes(0x3222, 0x5555);
    	MDrv_MFC_Write2Bytes(0x3224, 0x5555);
    	MDrv_MFC_WriteByte(0x3226, 0x05);
    }
    //thchen printf("**********Read status 2c45 = %x", MDrv_MFC_ReadByte(0x2C45));

    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
}


