void MDrv_MFC_FrameLockMode(U8 u8Mode)
{
    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugEnter);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWait);
    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBus);
    switch (u8Mode)
    {
        case 0: //disable controling    (no setting for Dclk, HV total)
            MDrv_MFC_WriteByteMask(0x1E4C, 0x80, 0xC0);        // bit [7:6]
            break;
        case 1: //frame lock
            MDrv_MFC_WriteByteMask(0x1E4C, 0x00, 0xC0);        // bit [7:6]
            break;
        case 2: //freerun
            MDrv_MFC_WriteByteMask(0x1E4C, 0x40, 0xC0);        // bit [7:6]
            break;
        default:
            MDrv_MFC_WriteByteMask(0x1E4C, 0x00, 0xC0);        // bit [7:6]
            break;
    }
    MDrv_MFC_SetSerialDebugMode(MFC_I2cUseBusN);
    MDrv_MFC_SetSerialDebugMode(MFC_CpuWaitN);
    MDrv_MFC_SetSerialDebugMode(MFC_SerialDebugExit);
}

